    function downloadVideo() {
      const url = document.getElementById('videoURL').value.trim();
      if (!url) {
        alert('Masukkan link terlebih dahulu.');
        return;
      }

      if (url.includes('facebook.com')) {
        window.open('https://snapsave.app/facebook?url=' + encodeURIComponent(url), '_blank');
      } else if (url.includes('instagram.com')) {
        window.open('https://igram.io?url=' + encodeURIComponent(url), '_blank');
      } else {
        alert('Link tidak valid. Harap masukkan link Facebook atau Instagram.');
      }
    }